UPDATE book SET
book_startday=now(),
book_totalrental=book_totalrental+1, book_rentalstate='대여중' WHERE book_no=#{bookNo}

mapper.select:
		SELECT (
					(
					SELECT price_value
					FROM price 
					WHERE member_level = ( 
											SELECT member_level 
											FROM member 
											WHERE member_no=#{memberNo} 
										) 
						) 
			      		 * 
		       		(
			       SELECT DATEDIFF(now(), rental_startday)  
			       FROM rental
			       WHERE rental_no=#{rentalNo}
			       )
			   )
